package com.perisatto.fiapprj.menuguru_product.application.usecase;

import static org.assertj.core.api.Assertions.assertThat;
import static org.assertj.core.api.Assertions.assertThatExceptionOfType;
import static org.mockito.ArgumentMatchers.any;
import static org.mockito.Mockito.times;
import static org.mockito.Mockito.verify;
import static org.mockito.Mockito.when;

import java.util.LinkedHashSet;
import java.util.Optional;
import java.util.Set;

import org.junit.jupiter.api.AfterEach;
import org.junit.jupiter.api.BeforeEach;
import org.junit.jupiter.api.Nested;
import org.junit.jupiter.api.Test;
import org.mockito.Mock;
import org.mockito.MockitoAnnotations;
import org.springframework.test.context.ActiveProfiles;

import com.perisatto.fiapprj.menuguru_product.application.interfaces.ProductRepository;
import com.perisatto.fiapprj.menuguru_product.application.usecases.ProductUseCase;
import com.perisatto.fiapprj.menuguru_product.domain.entities.Product;
import com.perisatto.fiapprj.menuguru_product.domain.entities.ProductType;
import com.perisatto.fiapprj.menuguru_product.handler.exceptions.NotFoundException;
import com.perisatto.fiapprj.menuguru_product.handler.exceptions.ValidationException;

@ActiveProfiles(value = "test")
public class ProductUseCaseTest {

	private String base64ProductImage = "";
	private String invalidBase64ProductImage = "/9j/4AAQSkZJRgABAQAAAQABAAD/4QBsRXhpZgAASUkqAAgAAAADADEBAgAHAAAAMgAAABICAwACAAAAAgACAGmHBAABAAAAOgAAAAAAAABHb29nbGUAAAMAAJAHAAQAAAAwMjIwAqAEAAEAAACQAQAAA6AEAAEAAACQAQAAAAAAAP/bAIQAAwICCAgLCQgICAoKCQsHBwgHBwgHCAgHCAcIBwgICAcICAgHCAcICAcHBwcHCgcHBwgJCQkHBwwMCggMBwgJCAEDBAQGBQYKBgYKDQ0MD";
	private String invalidFormatBase64Image = "";

	private ProductUseCase productUseCase;

	@Mock
	private ProductRepository productRepository;

	AutoCloseable openMocks;

	@BeforeEach
	void setUp() {
		openMocks = MockitoAnnotations.openMocks(this);
		productUseCase = new ProductUseCase(productRepository);
	}

	@AfterEach
	void tearDown() throws Exception {
		openMocks.close();
	}

	@Nested
	class RegisterProduct {

		@Test
		void givenValidData_thenRegisterProduct() throws Exception {
			Product product = getProduct();

			when(productRepository.createProduct(any(Product.class)))
			.thenReturn(product);

			productUseCase.createProduct(product.getName(), product.getProductType().toString(), product.getDescription(), product.getPrice(), product.getImage());

			verify(productRepository, times(1)).createProduct(any(Product.class));
		}

		@Test
		void givenInvalidProductType_thenRefusesRegisterProduct() throws Exception {
			Product product = getProduct();

			try {
				productUseCase.createProduct(product.getName(), "ENTRADA", product.getDescription(), product.getPrice(), product.getImage());
			} catch (Exception e) {
				assertThatExceptionOfType(ValidationException.class);
				verify(productRepository, times(0)).createProduct(any(Product.class));
			}
		}

		private Product getProduct() throws Exception {

			String description = "O x-bacon é um sanduíche irresistível que une o sabor intenso do bacon crocante com queijo derretido"
					+ ", alface, tomate e um suculento hambúrguer, tudo envolto em um pão macio e tostado. "
					+ "Uma explosão de sabores em cada mordida!";


			Product product = new Product("X-Bacon", ProductType.ACOMPANHAMENTO, description, 10.0, base64ProductImage);
			product.setId(10L);

			return product;
		}
	}

	@Nested
	class RetrieveProduct {

		@Test
		void givenValidaId_thenRetrieveProduct() throws Exception {
			Product product = getProduct();

			when(productRepository.getProductById(any(Long.class)))
			.thenReturn(Optional.of(product));

			productUseCase.getProduct(10L);

			verify(productRepository, times(1)).getProductById(any(Long.class));
		}

		@Test
		void givenInvalidId_thenRefusesRetrieveProduct() throws Exception {

			when(productRepository.getProductById(any(Long.class)))
			.thenReturn(Optional.empty());

			try {
				productUseCase.getProduct(10L);				
			} catch (Exception e) {
				assertThatExceptionOfType(NotFoundException.class);
				verify(productRepository, times(1)).getProductById(any(Long.class));
			}
		}

		@Test
		void listAllProducts() throws Exception {
			when(productRepository.findAll(any(), any(), any()))
			.thenAnswer(i -> {
				Set<Product> result = new LinkedHashSet<Product>();
				Product productData1 = getProduct();
				Product productData2 = getProduct();
				result.add(productData1);
				result.add(productData2);
				return result;
			});

			Set<Product> result = productUseCase.findAllProducts(null, null, null);

			assertThat(result.size()).isEqualTo(2);
		}

		@Test
		void givenInvalidProductType_thenRefusesRetrieveProduct() {
			try {
				productUseCase.findAllProducts(null, null, "ENTRADA");
			} catch (Exception e) {
				assertThatExceptionOfType(ValidationException.class);
			}
		}

		@Test
		void givenInvalidPage_thenRefusesRetrieveProduct() {
			try {
				productUseCase.findAllProducts(null, 0, null);
			} catch (Exception e) {
				assertThatExceptionOfType(ValidationException.class);
			}
		}

		@Test
		void givenInvalidLimit_thenRefusesRetrieveProduct() {
			try {
				productUseCase.findAllProducts(-1, null, null);
			} catch (Exception e) {
				assertThatExceptionOfType(ValidationException.class);
			}

			try {
				productUseCase.findAllProducts(60, null, null);
			} catch (Exception e) {
				assertThatExceptionOfType(ValidationException.class);
			}
		}

		private Product getProduct() throws Exception {

			String description = "O x-bacon é um sanduíche irresistível que une o sabor intenso do bacon crocante com queijo derretido"
					+ ", alface, tomate e um suculento hambúrguer, tudo envolto em um pão macio e tostado. "
					+ "Uma explosão de sabores em cada mordida!";


			Product product = new Product("X-Bacon", ProductType.ACOMPANHAMENTO, description, 10.0, base64ProductImage);
			product.setId(10L);

			return product;
		}
	}

	@Nested
	class UpdateProduct {

		@Test
		void givenValidData_thenUpdatesProduct() throws Exception {
			Product product = getProduct();

			when(productRepository.getProductById(any(Long.class)))
			.thenReturn(Optional.of(product));

			when(productRepository.updateProduct(any(Product.class)))
			.thenReturn(Optional.of(product));

			productUseCase.updateProduct(product.getId(), product.getName(), product.getProductType().toString(), product.getDescription(), product.getPrice(), product.getImage());

			verify(productRepository, times(1)).updateProduct(any(Product.class));
		}

		@Test
		void givenOnlyName_thenUpdatesProduct() throws Exception {
			Product product = getProduct();

			when(productRepository.getProductById(any(Long.class)))
			.thenReturn(Optional.of(product));

			when(productRepository.updateProduct(any(Product.class)))
			.thenReturn(Optional.of(product));

			productUseCase.updateProduct(product.getId(), product.getName(), null, null, null, null);

			verify(productRepository, times(1)).updateProduct(any(Product.class));
		}

		@Test
		void givenOthersThanName_thenUpdatesProduct() throws Exception {
			Product product = getProduct();

			when(productRepository.getProductById(any(Long.class)))
			.thenReturn(Optional.of(product));

			when(productRepository.updateProduct(any(Product.class)))
			.thenReturn(Optional.of(product));

			productUseCase.updateProduct(product.getId(), null, product.getProductType().toString(), product.getDescription(), product.getPrice(), product.getImage());

			verify(productRepository, times(1)).updateProduct(any(Product.class));
		}

		@Test
		void givenNoData_thenRefusesUpdatesProduct() throws Exception {
			Product product = getProduct();

			when(productRepository.getProductById(any(Long.class)))
			.thenReturn(Optional.of(product));

			when(productRepository.updateProduct(any(Product.class)))
			.thenReturn(Optional.of(product));

			try {
				productUseCase.updateProduct(product.getId(), null, null, null, null, null);
			} catch (Exception e) {
				assertThatExceptionOfType(ValidationException.class);
				verify(productRepository, times(0)).updateProduct(any(Product.class));
			}
		}

		@Test
		void whenThereIsDatabaseFail_thenUpdatesProduct() throws Exception {
			Product product = getProduct();

			when(productRepository.getProductById(any(Long.class)))
			.thenReturn(Optional.of(product));

			when(productRepository.updateProduct(any(Product.class)))
			.thenReturn(Optional.empty());

			try {
				productUseCase.updateProduct(product.getId(), product.getName(), product.getProductType().toString(), product.getDescription(), product.getPrice(), product.getImage());
			} catch (Exception e) {
				assertThatExceptionOfType(Exception.class);
			}
		}

		@Test
		void givenInvalidId_thenRefusesUpdatesProduct() throws Exception {
			Product product = getProduct();

			when(productRepository.getProductById(any(Long.class)))
			.thenReturn(Optional.empty());

			try {
				productUseCase.updateProduct(product.getId(), product.getName(), product.getProductType().toString(), product.getDescription(), product.getPrice(), product.getImage());
			} catch (Exception e) {
				assertThatExceptionOfType(NotFoundException.class);
				verify(productRepository, times(0)).updateProduct(any(Product.class));
			}
		}

		private Product getProduct() throws Exception {

			String description = "O x-bacon é um sanduíche irresistível que une o sabor intenso do bacon crocante com queijo derretido"
					+ ", alface, tomate e um suculento hambúrguer, tudo envolto em um pão macio e tostado. "
					+ "Uma explosão de sabores em cada mordida!";


			Product product = new Product("X-Bacon", ProductType.ACOMPANHAMENTO, description, 10.0, base64ProductImage);
			product.setId(10L);

			return product;
		}
	}

	@Nested
	class DeleteProduct {

		@Test
		void givenValidId_thenDeleteProduct() throws Exception {
			Product product = getProduct();

			when(productRepository.getProductById(any(Long.class)))
			.thenReturn(Optional.of(product));

			when(productRepository.deleteProduct(any(Long.class)))
			.thenReturn(true);

			productUseCase.deleteProduct(10L);

			verify(productRepository, times(1)).deleteProduct(any(Long.class));
		}

		@Test
		void givenInvalidId_thenRefusesDeleteProduct() throws Exception {			
			when(productRepository.getProductById(any(Long.class)))
			.thenReturn(Optional.empty());
			
			try {	
				productUseCase.deleteProduct(10L);

			} catch (Exception e) {
				assertThatExceptionOfType(NotFoundException.class);
				verify(productRepository, times(0)).deleteProduct(any(Long.class));
			}
		}

		private Product getProduct() throws Exception {

			String description = "O x-bacon é um sanduíche irresistível que une o sabor intenso do bacon crocante com queijo derretido"
					+ ", alface, tomate e um suculento hambúrguer, tudo envolto em um pão macio e tostado. "
					+ "Uma explosão de sabores em cada mordida!";


			Product product = new Product("X-Bacon", ProductType.ACOMPANHAMENTO, description, 10.0, base64ProductImage);
			product.setId(10L);

			return product;
		}
	}	
}

